type Operator = StringOperator | StringFacetFieldOperator | NumericOperator;
export type StringOperator = 'contains' | 'isExactly';
export type StringFacetFieldOperator = 'contains' | 'isExactly' | 'phoneticMatch' | 'fuzzyMatch' | 'wildcardMatch' | 'differentThan' | 'regexMatch';
export type NumericOperator = 'isExactly' | 'lowerThan' | 'lowerThanOrEqual' | 'greaterThan' | 'greaterThanOrEqual';
export declare function getOperatorSymbol(operator: Operator): string;
export {};
