import { baseInsightRequest, pickNonInsightParams, } from '../insight-params.js';
export const buildInsightQueryRequest = (req) => {
    return {
        ...baseInsightRequest(req, 'POST', 'application/json', '/search'),
        requestParams: pickNonInsightParams(req),
    };
};
export const buildInsightQuerySuggestRequest = (req) => {
    return {
        ...baseInsightRequest(req, 'POST', 'application/json', '/querysuggest'),
        requestParams: pickNonInsightParams(req),
    };
};
