import { executeSearch } from '../../../features/insight-search/insight-search-actions.js';
import { browseResults, logPagerResize, } from '../../../features/pagination/pagination-analytics-actions.js';
import { buildCoreResultsPerPage, } from '../../core/results-per-page/headless-core-results-per-page.js';
/**
 * Creates an insight `ResultsPerPage` controller instance.
 *
 * @param engine - The insight engine.
 * @param props - The configurable `ResultsPerPage` properties.
 * @returns A `ResultsPerPage` controller instance.
 *
 * @group Controllers
 * @category ResultsPerPage
 */
export function buildResultsPerPage(engine, props = {}) {
    const coreController = buildCoreResultsPerPage(engine, props);
    const { dispatch } = engine;
    return {
        ...coreController,
        get state() {
            return {
                ...coreController.state,
            };
        },
        set(num) {
            coreController.set(num);
            dispatch(executeSearch({ legacy: logPagerResize(), next: browseResults() }));
        },
    };
}
