import { StringValue } from '@coveo/bueno';
import { createAction } from '@reduxjs/toolkit';
import { nonEmptyString, requiredNonEmptyString, validatePayload, } from '../../utils/validate-payload.js';
export const setCaseAssistConfiguration = createAction('caseAssistConfiguration/set', (payload) => validatePayload(payload, {
    caseAssistId: requiredNonEmptyString,
    locale: nonEmptyString,
    proxyBaseUrl: new StringValue({ required: false, url: true }),
}));
