import { searchReducer as search } from '../../features/search/search-slice.js';
import { registerQuerySuggest, } from '../query-suggest/query-suggest-actions.js';
import { fetchQuerySuggestions, } from './insight-search-actions.js';
import { executeSearch, fetchFacetValues, fetchMoreResults, fetchPage, } from './legacy/insight-search-actions.js';
/**
 * Loads the `search` reducer and returns possible action creators.
 *
 * @param engine - The headless engine.
 * @returns An object holding the action creators.
 */
export function loadInsightSearchActions(engine) {
    engine.addReducers({ search });
    return {
        executeSearch,
        fetchFacetValues,
        fetchMoreResults,
        fetchPage,
        fetchQuerySuggestions,
        registerQuerySuggest,
    };
}
