import { buildProductListing, } from '../../../../controllers/commerce/product-listing/headless-product-listing.js';
import { buildSearch, } from '../../../../controllers/commerce/search/headless-search.js';
import { ensureAtLeastOneSolutionType } from '../../controller-utils.js';
import { SolutionType } from '../../types/controller-constants.js';
/**
 * Defines a `ProductList` controller instance.
 * @group Definers
 *
 * @returns The `ProductList` controller definition.
 */
export function defineProductList(options) {
    ensureAtLeastOneSolutionType(options);
    return {
        listing: true,
        search: true,
        ...options,
        build: (engine, solutionType) => solutionType === SolutionType.listing
            ? buildProductListing(engine)
            : buildSearch(engine),
    };
}
