export function buildMockRecommendationsResponse(response = {}) {
    return {
        response: {
            headline: response.headline ?? '',
            pagination: response.pagination ?? {
                page: 0,
                perPage: 0,
                totalEntries: 0,
                totalPages: 0,
            },
            products: response.products ?? [],
            responseId: response.responseId ?? '',
        },
    };
}
